package day09_SwitchCase22;

public class LOOPS {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for (int i = 10; i < 10; i++) {
	System.out.println("mesaj" +i);
}
int i=10;
while(i<10)  {
	System.out.println("mesaj"+i);
	i++;
}
	 i=10;
do {System.out.println("ikinci mesaj"+i);
	i++;
	} while(i<10);

}
}
